class RedBean_Can implements Iterator , ArrayAccess , SeekableIterator , Countable { private $collectionIDs = null; private $type = null; private $pointer = 0; private $num = 0; private $provider = null; public function __construct( RedBean_ToolBox_ModHub $provider, $type="", $collection = array() ) { $this->provider=$provider; $this->collectionIDs = $collection; $this->type = $type; $this->num = count( $this->collectionIDs ); } public function wrap( $bean, $prefix=false, $suffix=false ) { if (!$prefix) { $prefix = RedBean_Setup_Namespace_PRFX; } if (!$suffix) { $suffix = RedBean_Setup_Namespace_SFFX; } $dclass = $prefix.$this->type.$suffix; $deco = new $dclass( floatval( $bean->id ) ); $deco->setData( $bean ); return $deco; } public function count() { return $this->num; } public function getBeans() { $rows = $this->provider->getBeanStore()->fastloader( $this->type, $this->collectionIDs ); $beans = array(); if (is_array($rows)) { foreach( $rows as $row ) { $beans[] = $this->wrap( $this->provider->getById( $this->type, $row["id"] , $row) ); } } return $beans; } public function slice( $begin=0, $end=0 ) { $this->collectionIDs = array_slice( $this->collectionIDs, $begin, $end); $this->num = count( $this->collectionIDs ); } public function current() { if (isset($this->collectionIDs[$this->pointer])) { $id = $this->collectionIDs[$this->pointer]; return $this->wrap( $this->provider->getById( $this->type, $id ) ); } else { return null; } } public function key() { return $this->pointer; } public function next() { return ++$this->pointer; } public function prev() { if ($this->pointer > 0) { return ++$this->pointer; }else { return 0; } } public function rewind() { $this->pointer=0; return 0; } public function seek( $seek ) { $this->pointer = (int) $seek; return $this; } public function valid() { return ($this->num > ($this->pointer+1)); } public function hasMoreBeans() { return $this->valid(); } public function offsetSet($offset, $value) { $this->collectionIDs[$offset] = $value; } public function offsetExists($offset) { return isset($this->collectionIDs[$offset]); } public function offsetUnset($offset) { unset($this->collectionIDs[$offset]); } public function offsetGet($offset) { if (isset($this->collectionIDs[$offset])) { $id = $this->collectionIDs[$offset]; return $this->wrap( $this->provider->getById( $this->type, $id ) ); } else { return null; } } public function getList() { $list = array(); $beans = $this->getBeans(); foreach($beans as $bean) { $list[] = $bean->exportAsArr(); } return $list; } public function reverse() { $this->collectionIDs = array_reverse($this->collectionIDs, false); return $this; } }
class RedBean_DBAdapter extends RedBean_Observable implements RedBean_Tool { private $db = null; private $sql = ""; public function __construct($database) { $this->db = $database; } public function getSQL() { return $this->sql; } public function escape( $sqlvalue ) { return $this->db->Escape($sqlvalue); } public function exec( $sql , $noevent=false) { if (!$noevent){ $this->sql = $sql; $this->signal("sql_exec", $this); } return $this->db->Execute( $sql ); } public function get( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetAll( $sql ); } public function getRow( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetRow( $sql ); } public function getCol( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); return $this->db->GetCol( $sql ); } public function getCell( $sql ) { $this->sql = $sql; $this->signal("sql_exec", $this); $arr = $this->db->GetCol( $sql ); if ($arr && is_array($arr)) return ($arr[0]); else return false; } public function getInsertID() { return $this->db->getInsertID(); } public function getAffectedRows() { return $this->db->Affected_Rows(); } public function getDatabase() { return $this->db; } public function getErrorMsg() { return $this->db->Errormsg(); } }
class RedBean_Decorator extends RedBean_Observable implements IteratorAggregate { protected $data = null; protected $type = ""; protected $problems = array(); protected $provider = null; public function __construct( RedBean_OODB $provider, $type=false, $id=0) { $this->provider = $provider; $id = floatval( $id ); if (!$type) { throw new Exception("Undefined bean type"); } else { $this->type = $this->provider->getToolBox()->getFilter()->table($type); if ($id > 0) { $this->data = $this->provider->getById( $this->type, $id); } else { $this->data = $this->provider->dispense( $this->type ); } } } private static function getStaticProvider() { return RedBean_OODB::getInstance(); } public function free( $property ) { $this->signal("deco_free", $this); $this->provider->dropColumn( $this->type, $property ); } public function importFromPost( $selection=null ) { $this->signal("deco_importpost", $this); if (!$selection) { $selection = array_keys($_POST); } if (is_string($selection)) { $selection = explode(",",$selection); } if ($selection && is_array($selection) && count($selection) > 0) { foreach( $selection as $field ) { $setter = "set".ucfirst( $field ); if (isset( $_POST[$field] )) { $resp = $this->$setter( $_POST[ $field ] ); } } } return $this; } public function import( $arr ) { $this->signal("deco_import", $this); foreach( $arr as $key=>$val ) { $setter = "set".ucfirst( $key ); $resp = $this->$setter( $val ); } return $this; } public function __call( $method, $arguments ) { return $this->command( $method, $arguments ); } public function __get( $name ) { $this->signal("deco_get", $this); $name = $this->provider->getToolBox()->getFilter()->property($name, true); return isset($this->data->$name) ? $this->data->$name : null; } public function __set( $name, $value ) { $this->signal("deco_set", $this); $name = $this->provider->getToolBox()->getFilter()->property($name); $this->data->$name = $value; } public function command( $method, $arguments ) { if (strpos( $method,"set" ) === 0) { $prop = substr( $method, 3 ); $prop = $this->provider->getToolBox()->getFilter()->property($prop); $this->$prop = $arguments[0]; return $this; } elseif (strpos($method,"getRelated")===0) { $this->signal("deco_get", $this); $prop = $this->provider->getToolBox()->getFilter()->table( substr( $method, 10 ) ); $beans = $this->provider->getAssoc( $this->data, $prop ); $decos = array(); $dclass = RedBean_Setup_Namespace_PRFX.$prop.RedBean_Setup_Namespace_SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } return $decos; } elseif (strpos( $method, "get" ) === 0) { $prop = substr( $method, 3 ); $prop = $this->provider->getToolBox()->getFilter()->property($prop, true); return $this->$prop; } elseif (strpos( $method, "is" ) === 0) { $prop = $this->provider->getToolBox()->getFilter()->property( substr( $method, 2 ) ); if (!isset($this->data->$prop)) { $this->signal("deco_get",$this); return false; } return ($this->data->$prop ? TRUE : FALSE); } else if (strpos($method,"add") === 0) { $this->signal("deco_add",$this); $deco = $arguments[0]; $bean = $deco->getData(); $this->provider->associate($this->data, $bean); return $this; } else if (strpos($method,"remove")===0) { $this->signal("deco_remove",$this); $deco = $arguments[0]; $bean = $deco->getData(); $this->provider->unassociate($this->data, $bean); return $this; } else if (strpos($method,"attach")===0) { $this->signal("deco_attach",$this); $deco = $arguments[0]; $bean = $deco->getData(); $this->provider->addChild($this->data, $bean); return $this; } else if (strpos($method,"clearRelated")===0) { $this->signal("deco_clearrelated",$this); $type = $this->provider->getToolBox()->getFilter()->table( substr( $method, 12 ) ); $this->provider->deleteAllAssocType($type, $this->data); return $this; } else if (strpos($method,"numof")===0) { $this->signal("deco_numof",$this); $type = $this->provider->getToolBox()->getFilter()->table( substr( $method, 5 ) ); return $this->provider->numOfRelated($type, $this->data); } } public function belongsTo( $deco ) { $this->signal("deco_belongsto", $this); $this->provider->deleteAllAssocType($deco->getType(), $this->data); $this->provider->associate($this->data, $deco->getData()); } public function exclusiveAdd( $deco ) { $this->signal("deco_exclusiveadd", $this); $this->provider->deleteAllAssocType($this->type,$deco->getData()); $this->provider->associate($deco->getData(), $this->data); } public function parent() { $this->signal("deco_parent", $this); $beans = $this->provider->getParent( $this->data ); if (count($beans) > 0 ) $bean = array_pop($beans); else return null; $dclass = RedBean_Setup_Namespace_PRFX.$this->type.RedBean_Setup_Namespace_SFFX; $deco = new $dclass(); $deco->setData( $bean ); return $deco; } public function siblings() { $this->signal("deco_siblings", $this); $beans = $this->provider->getParent( $this->data ); if (count($beans) > 0 ) { $bean = array_pop($beans); } else { return null; } $beans = $this->provider->getChildren( $bean ); $decos = array(); $dclass = RedBean_Setup_Namespace_PRFX.$this->type.RedBean_Setup_Namespace_SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { if ($b->id != $this->data->id) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } } return $decos; } public function children() { $this->signal("deco_children", $this); $beans = $this->provider->getChildren( $this->data ); $decos = array(); $dclass = RedBean_Setup_Namespace_PRFX.$this->type.RedBean_Setup_Namespace_SFFX; if ($beans && is_array($beans)) { foreach($beans as $b) { $d = new $dclass(); $d->setData( $b ); $decos[] = $d; } } return $decos; } public function hasParent( $deco ) { $me = $this; while( $parent = $me->parent() ) { if ($deco->getID() == $parent->getID()) { return true; } else { $me = $parent; } } return false; } public function hasChild( $deco ) { $nodes = array($this); while($node = array_shift($nodes)) { if ($node->getID() == $deco->getID() && ($node->getID() != $this->getID())) { return true; } if ($children = $node->children()) { $nodes = array_merge($nodes, $children); } } return false; } public function hasSibling( $deco ) { $siblings = $this->siblings(); foreach( $siblings as $sibling ) { if ($sibling->getID() == $deco->getID()) { return true; } } return false; } public function copy() { $this->signal("deco_copy", $this); $clone = new self( $this->provider, $this->type, 0 ); $clone->setData( $this->getData() ); return $clone; } public function clearAllRelations() { $this->signal("deco_clearrelations", $this); $this->provider->deleteAllAssoc( $this->getData() ); } public function getData() { return $this->data; } public function setData( $data ) { $this->data = $data; } public function save() { $this->signal("deco_save", $this); return $this->provider->set( $this->data ); } public static function delete( $deco ) { self::getStaticProvider()->trash( $deco->getData() ); } public function destroy() { $this->provider->trash( $this->getData() ); } public function lock() { $this->provider->openBean($this->getData()); } public function unlock() { $this->provider->closeBean( $this->getData()); } public static function close( $deco ) { self::getStaticProvider()->closeBean( $deco->getData() ); } public static function make( $type="", $id ){ return new RedBean_Decorator( self::getStaticProvider(), $type, $id ); } public function exportTo( &$bean, $overridebean=false ) { foreach($this->data as $prop=>$value) { if (is_object($overridebean) && isset($overridebean->$prop)) { $value = $overridebean->$prop; } elseif (is_array($overridebean) && isset($overridebean[$prop])) { $value = $overridebean[$prop]; } if (is_object($value)){ $value = $value->getID(); } if (is_object($bean)) { $bean->$prop = $value; } elseif (is_array($bean)) { $bean[$prop] = $value; } } return $bean; } public function exportAsArr() { $arr = array(); foreach($this->data as $prop=>$value) { if ($value instanceof RedBean_Decorator){ $value = $value->getID(); } $arr[ $prop ] = $value; } return $arr; } public static function find( $deco, $filter, $start=0, $end=100, $orderby=" id ASC ", $extraSQL=false ) { $rf = new RedBean_Mod_Filter_Strict; if (!is_array($filter)) { return array(); } if (count($filter)<1) { return array(); } $filters = array(); foreach($filter as $key=>$f) { $filters[$rf->property($key)] =$f; if (!in_array($f,array("=","!=","<",">","<=",">=","like","LIKE"))) { throw new ExceptionInvalidFindOperator(); } } $beans = self::getStaticProvider()->find( $deco->getData(), $filters, $start, $end, $orderby, $extraSQL ); $decos = array(); $dclass = RedBean_Setup_Namespace_PRFX.$deco->type.RedBean_Setup_Namespace_SFFX; foreach( $beans as $bean ) { $decos[ $bean->id ] = new $dclass( floatval( $bean->id ) ); $decos[ $bean->id ]->setData( $bean ); } return $decos; } public function getIterator() { $o = new ArrayObject($this->data); return $o->getIterator(); } public function isReadOnly() { try{ $this->provider->openBean($this->data, true); } catch(RedBean_Exception_FailedAccessBean $e){ return true; } return false; } }
interface RedBean_Driver { public static function getInstance( $host, $user, $pass, $dbname ); public function GetAll( $sql ); public function GetCol( $sql ); public function GetCell( $sql ); public function GetRow( $sql ); public function ErrorNo(); public function Errormsg(); public function Execute( $sql ); public function Escape( $str ); public function GetInsertID(); public function Affected_Rows(); public function setDebugMode( $tf ); public function GetRaw(); }
class Redbean_Exception extends Exception{}
abstract class RedBean_Mod implements RedBean_Tool { protected $provider; public function __construct(RedBean_ToolBox $provider) { $this->provider = $provider; } }
class RedBean_Observable { private $observers = array(); public function addEventListener( $eventname, RedBean_Observer $observer ) { if (!isset($this->observers[ $eventname ])) { $this->observers[ $eventname ] = array(); } $this->observers[ $eventname ][] = $observer; } public function signal( $eventname ) { if (!isset($this->observers[ $eventname ])) { $this->observers[ $eventname ] = array(); } foreach($this->observers[$eventname] as $observer) { $observer->onEvent( $eventname, $this ); } } }
interface RedBean_Observer { public function onEvent( $eventname, RedBean_Observable $o ); }
class RedBean_OODB { public $pkey = false; private $rollback = false; private static $me = null; private $engine = "myisam"; private $frozen = false; private $toolbox = null; public function initWithToolBox( RedBean_ToolBox_ModHub $toolbox ) { $this->toolbox = $toolbox; $db = $this->toolbox->getDatabase(); $writer = $this->toolbox->getWriter(); if ($this->engine === "innodb") { $db->exec($writer->getQuery("prepare_innodb")); $db->exec($writer->getQuery("starttransaction")); } else if ($this->engine === "myisam") { $db->exec($writer->getQuery("prepare_myisam")); } if (!$this->frozen) { $db->exec($writer->getQuery("clear_dtyp")); $db->exec($writer->getQuery("setup_dtyp")); $db->exec($writer->getQuery("setup_locking")); $db->exec($writer->getQuery("setup_tables")); } if (!$this->pkey) { $this->pkey = str_replace(".","",microtime(true)."".mt_rand()); } return true; } public function __destruct() { $this->releaseAllLocks(); $this->toolbox->getDatabase()->exec( $this->toolbox->getWriter()->getQuery("destruct", array("engine"=>$this->engine,"rollback"=>$this->rollback)) ); } public function isFrozen() { return (boolean) $this->frozen; } public static function getInstance( RedBean_ToolBox_ModHub $toolbox = NULL ) { if (self::$me === null) { self::$me = new RedBean_OODB; } if ($toolbox) self::$me->initWithToolBox( $toolbox ); return self::$me; } public function getToolBox() { return $this->toolbox; } public function getEngine() { return $this->engine; } public function setEngine( $engine ) { if ($engine=="myisam" || $engine=="innodb") { $this->engine = $engine; } else { throw new Exception("Unsupported database engine"); } return $this->engine; } public static function rollback() { $this->rollback = true; } public function set( RedBean_OODBBean $bean ) { return $this->toolbox->getBeanStore()->set($bean); } public function inferType( $v ) { return $this->toolbox->getScanner()->type( $v ); } public function getType( $sqlType ) { return $this->toolbox->getScanner()->code( $sqlType ); } public function freeze() { $this->frozen = true; } public function unfreeze() { $this->frozen = false; } public function showTables( $all=false ) { return $this->toolbox->getTableRegister()->getTables($all); } public function addTable( $tablename ) { return $this->toolbox->getTableRegister()->register( $tablename); } public function dropTable( $tablename ) { return $this->toolbox->getTableRegister()->unregister( $tablename ); } public function releaseAllLocks() { $this->toolbox->getDatabase()->exec($this->toolbox->getWriter()->getQuery("release",array("key"=>$this->pkey))); } public function openBean( $bean, $mustlock=false) { $this->toolbox->getBeanChecker()->check( $bean ); $this->toolbox->getLockManager()->openBean( $bean, $mustlock ); } public function getById($type, $id, $data=false) { return $this->toolbox->getBeanStore()->get($type,$id,$data); } public function exists($type,$id) { return $this->toolbox->getBeanStore()->exists($type, $id); } public function numberof($type) { return $this->toolbox->getBeanStore()->numberof( $type ); } public function distinct($type, $field) { return $this->toolbox->getLister()->distinct( $type, $field ); } private function stat($type,$field,$stat="sum") { return $this->toolbox->getLister()->stat( $type, $field, $stat); } public function sumof($type,$field) { return $this->stat( $type, $field, "sum"); } public function avgof($type,$field) { return $this->stat( $type, $field, "avg"); } public function minof($type,$field) { return $this->stat( $type, $field, "min"); } public function maxof($type,$field) { return $this->stat( $type, $field, "max"); } public function resetAll() { $sql = $this->toolbox->getWriter()->getQuery("releaseall"); $this->toolbox->getDatabase()->exec( $sql ); return true; } public function getBySQL( $rawsql, $slots, $table, $max=0 ) { return $this->toolbox->getSearch()->sql( $rawsql, $slots, $table, $max ); } public function find(RedBean_OODBBean $bean, $searchoperators = array(), $start=0, $end=100, $orderby="id ASC", $extraSQL=false) { return $this->toolbox->getFinder()->find($bean, $searchoperators, $start, $end, $orderby, $extraSQL); } public function listAll($type, $start=false, $end=false, $orderby="id ASC", $extraSQL = false) { return $this->toolbox->getLister()->get($type, $start, $end, $orderby,$extraSQL); } public function associate( RedBean_OODBBean $bean1, RedBean_OODBBean $bean2 ) { return $this->toolbox->getAssociation()->link( $bean1, $bean2 ); } public function unassociate(RedBean_OODBBean $bean1, RedBean_OODBBean $bean2) { return $this->toolbox->getAssociation()->breakLink( $bean1, $bean2 ); } public function getAssoc(RedBean_OODBBean $bean, $targettype) { return $this->toolbox->getAssociation()->get( $bean, $targettype ); } public function trash( RedBean_OODBBean $bean ) { return $this->toolbox->getBeanStore()->trash( $bean ); } public function deleteAllAssoc( $bean ) { return $this->toolbox->getAssociation()->deleteAllAssoc( $bean ); } public function deleteAllAssocType( $targettype, $bean ) { return $this->toolbox->getAssociation()->deleteAllAssocType( $targettype, $bean ); } public function dispense( $type="StandardBean" ) { return $this->toolbox->getDispenser()->dispense( $type ); } public function addChild( RedBean_OODBBean $parent, RedBean_OODBBean $child ) { return $this->toolbox->getTree()->add( $parent, $child ); } public function getChildren( RedBean_OODBBean $parent ) { return $this->toolbox->getTree()->getChildren($parent); } public function getParent( RedBean_OODBBean $child ) { return $this->toolbox->getTree()->getParent($child); } public function removeChild(RedBean_OODBBean $parent, RedBean_OODBBean $child) { return $this->toolbox->getTree()->removeChild( $parent, $child ); } public function numofRelated( $type, RedBean_OODBBean $bean ) { return $this->toolbox->getAssociation()->numOfRelated( $type, $bean ); } public function generate( $classes, $prefix = false, $suffix = false ) { return $this->toolbox->getClassGenerator()->generate($classes,$prefix,$suffix); } public function clean() { return $this->toolbox->getGC()->clean(); } public function removeUnused( ) { if ($this->frozen) { return false; } return $this->toolbox->getGC()->removeUnused( $this, $this->toolbox->getDatabase(), $this->toolbox->getWriter() ); } public function dropColumn( $table, $property ) { return $this->toolbox->getGC()->dropColumn($table,$property); } public function trashAll($type) { $this->toolbox->getDatabase()->exec( $this->toolbox->getWriter()->getQuery("drop_type",array("type"=>$this->toolbox->getFilter()->table($type)))); } public static function gen($arg, $prefix = false, $suffix = false) { return self::getInstance()->generate($arg, $prefix, $suffix); } public static function keepInShape($gc = false ,$stdTable=false, $stdCol=false) { return self::getInstance()->getToolBox()->getOptimizer()->run($gc, $stdTable, $stdCol); } public static function kickstartDev( $gen, $dsn, $username="root", $password="", $debug=false ) { return RedBean_Setup::kickstartDev( $gen, $dsn, $username, $password, $debug ); } public static function kickstartFrozen( $gen, $dsn, $username="root", $password="" ) { return RedBean_Setip::kickstartFrozen( $gen, $dsn, $username, $password); } }
